async function main(){await sleep(1e3);var n=["Hello, there!","I'm Grzegorz Bronka, welcome to my website."];for(let e=0;e<n.length;e++)await typeSentence(n[e],40),await sleep(1e3),e+1<n.length&&await deleteSentence(20)}async function typeSentence(n,t){for(let e=0;e<n.length;e++)document.getElementById("friend-text").innerHTML+=n.charAt(e),await sleep(t)}async function deleteSentence(n){for(let e=document.getElementById("friend-text").innerHTML.length;0<=e;e--){var t=document.getElementById("friend-text").innerHTML;document.getElementById("friend-text").innerHTML=t.substring(0,e),await sleep(n)}}main();var con=document.getElementById("console"),visible=!0;function sleep(n){return new Promise(e=>setTimeout(e,n))}window.setInterval(function(){visible=!0===visible?!(con.className="console-underscore hidden"):(con.className="console-underscore",!0)},500);